# USAGE
# python stress_test.py

# import the necessary packages
from threading import Thread
import requests
import time
import random

# initialize the Keras REST API endpoint URL along with the input
# image path
KERAS_REST_API_URL = "http://localhost:8000/custom/paint"
# IMAGE_PATH = "jemma.png"

# initialize the number of requests for the stress test along with
# the sleep amount between requests
NUM_REQUESTS = 50
SLEEP_COUNT = 0.05


def call_predict_endpoint(n):
    # load the input image and construct the payload for the request
    # image = open(IMAGE_PATH, "rb").read()
    # payload = {"image": image}

    start = time.time()
    # print(start)

    # submit the request
    # r = requests.post(KERAS_REST_API_URL, files=payload).json()
    r = requests.post(KERAS_REST_API_URL, json={
        'compose_image_str': 'data:image/png;base64,',
        'id': random.randint(1, 1000),
        'user_id': 123,
        'standard_model_path': 'standard_model.hdf5'
    })

    # print(r)
    # ensure the request was sucessful
    if r.status_code == 200:
        print("[INFO] thread {} OK, use {}s".format(n, time.time() - start))

    # otherwise, the request failed
    else:
        print("[INFO] thread {} FAILED".format(n))


# loop over the number of threads
for i in range(0, NUM_REQUESTS):
    # start a new thread to call the API
    t = Thread(target=call_predict_endpoint, args=(i,))
    t.daemon = True
    t.start()
    time.sleep(SLEEP_COUNT)

# insert a long sleep so we can wait until the server is finished
# processing the images
time.sleep(300)
